// Import this package
import ProgressBar from './index.js';

var num = 0,
	total = 200;

// init a progress bar
var pb = new ProgressBar('Download Progress', total);
function downloading() {
	if (num <= total) {
		// update progress bar
		pb.render(num);

		num++;
		setTimeout(function () {
			downloading();
		}, 20);
	} else {
		return;
	}
}
downloading();
